import React, { Component } from "react";
import "./index.css";

export default class Item extends Component {
  render() {
    let { done, name } = this.props;
    return (
      <li>
        <label>
          <input type="checkbox" />
          <span> {name} </span>
        </label>
        <button className="btn btn-danger" style={{ display: done }}> 删除 </button>
         
       
      </li>
    );
  }
}
