import React, { Component } from "react";
import "./index.css";

export default class Header extends Component {


    addObj=e=>{
        console.log(e.target.value);
    }
  render() {
    return (
      <div className="todo-header">
        <input onKeyUp={this.addObj} type="text" placeholder="请输入你的任务名称，按回车键确认" />
      </div>
    );
  }
}
